import React, { useState } from 'react';
import ProductCard from './ProductCard';

const Product = () => {
  const [wishlist, setWishlist] = useState([]);

  const handleAddToWishlist = (productName) => {
    setWishlist([...wishlist, productName]);
  };

  return (
    <div className="app">
      <h1>My Products</h1>
      <ProductCard name="Product A" price={19.99} addToWishlist={() => handleAddToWishlist("Product A")} />
      <ProductCard name="Product B" price={29.99} addToWishlist={() => handleAddToWishlist("Product B")} />
      <ProductCard name="Product C" price={9.99} addToWishlist={() => handleAddToWishlist("Product C")} />
      <h2>My Wishlist:</h2>
      <ul>
        {wishlist.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
};

export default Product;
