import React, { useState } from "react";
import "/media/hp/107 GB Volume/WebDev/Projects/giftbazar/src/Components/Product/ProductCard/ProductCard.css";

function ProductCard() {
  const [isFlipped, setIsFlipped] = useState(false);
  const [quantity, setQuantity] = useState(1);

  const handleHover = () => {
    setIsFlipped(true);
  };

  const handleLeave = () => {
    setIsFlipped(false);
  };

  const handleViewDetailsClick = () => {
    setIsFlipped(true);

    setTimeout(() => {
      setIsFlipped(false);
      setQuantity(1);
    }, 550);
  };

  const handleQuantityChange = (value) => {
    setQuantity((prevQuantity) => prevQuantity + value);
  };

  const handleMinusClick = () => {
    handleQuantityChange(-1);
  };

  const handlePlusClick = () => {
    handleQuantityChange(1);
  };

  const price = 39 * quantity;

  return (
    <div
      id="product-card"
      className={isFlipped ? "animate flip-180" : ""}
      onMouseOver={handleHover}
      onMouseLeave={handleLeave}
    >
      <div id="product-front" className="product">
        <img src="https://picsum.photos/335/465" alt="product" />
      </div>
      <div id="product-back" className="product">
        <div id="shadow"></div>
        <div id="carousel">
          <ul>
            <li>
              <img src="https://picsum.photos/335/465" alt="product" />
            </li>
            <li>
              <img src="https://picsum.photos/335/465" alt="product" />
            </li>
            <li>
              <img src="https://picsum.photos/335/465" alt="product" />
            </li>
          </ul>
        </div>
      </div>
      <div className="product-details">
        <h1>Product Name</h1>
        <p className="price" id="price">
          ${price}
        </p>
        <div className="quantity">
          <button className="minus" onClick={handleMinusClick}>
            -
          </button>
          <span className="number">{quantity}</span>
          <button className="plus" onClick={handlePlusClick}>
            +
          </button>
        </div>
        <button id="view_details" onClick={handleViewDetailsClick}>
          View Details
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
