import React from 'react';

const ProductCard = ({ name, price, imageSrc, onBuyClick, onInfoClick }) => {
  return (
    <div className="product-card">
      <img src={imageSrc} alt={name} className="product-card-image" />
      <div className="product-card-info">
        <h2 className="product-card-name">{name}</h2>
        <p className="product-card-price">{price}</p>
        <button className="product-card-buy-button" onClick={onBuyClick}>Buy</button>
        <button className="product-card-info-button" onClick={onInfoClick}>Info</button>
      </div>
    </div>
  );
};

export default ProductCard;
