import React, { useState } from 'react';
import ProductCard from './ProductCard';

function Product() {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  return (
    <div className="App">
      <h1>My Online Store</h1>
      <ProductCard name="Product Name" price="10.99" rating="4" addToCart={addToCart} />
      {/* more ProductCards here */}
      <div className="cart">
        <h2>Cart</h2>
        <ul>
          {cartItems.map((item, i) => (
            <li key={i}>{item.name} - ${item.price} - Quantity: {item.quantity}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Product;
